import { useEffect } from "react";
import './App.css';
import SearchIcon from './search.svg'

//'http://www.omdbapi.com?apikey=d23ea893'
const API_URL = "http://www";

const movie1 = {
    "Title": "Spiderman",
    "Year": "2012",
    "imdbID": "tt258"
}

const App = () => {
    const searchMovies = async (title) => {
        const repsonse = await fetch(`${API_URL}&s=${title}`);
        const data = await repsonse.json();
        console.log(data);
    }
    useEffect(() => {
        searchMovies('Batman')
    },[])
    return (
        <div>
            <h1>MovieLand</h1>
            <div className="search">
                <input 
                    placeholder = "search for movies" 
                    value = "Batman"
                    onChange={() => {}}
                />
                <img
                    src={SearchIcon}
                    alt="search"
                    onClick={() => {}}
                />
            </div>
            <div className="container">
                <div className="movie">
                    <p>{movie1.Year}</p>
                </div>
                <div>
                    <img src={movie1.Poster} alt={movie1.Title}/>
                </div>
            </div>
        
        </div>
    )
}

export default App;
